counter = 0

in_buffer = allocate(shape=(buffer_size,), dtype=np.int32)
out_buffer = allocate(shape=(1,), dtype=np.int32)

# Open the CSV file for reading
csv_filename = '/home/xilinx/AI/post_processed_features_old_null_5f_2.csv'

with open(csv_filename, 'r') as csv_file:
    csv_reader = csv.reader(csv_file)

    start_time = time.time()
    for index, row in enumerate(csv_reader):
        # Read features from CSV row and convert to floats
        features = [float(value) for value in row]
        in_buffer[:] = features

        # Send data through DMA
        dma_send.transfer(in_buffer)
        dma_recv.transfer(out_buffer)

        dma_send.wait()
        dma_recv.wait()

        # Print received data
        received_data = int(out_buffer[0])
        # print("Received data:", received_data, "Expected data:", expected_preds[index])

        if received_data == expected_preds[index]:
            counter = counter + 1
    end_time = time.time()
    time_taken = end_time - start_time
    print("Final accuracy:", (counter*100) / DATASET_SIZE)
    print("Time taken:", time_taken, "seconds")

