from pynq import Overlay
from pynq import allocate
import numpy as np
import csv
import time
import os
import subprocess

FEATURES = 5
SENSOR_NUM = 6
DATASET_SIZE = 1839
BITSTREAM_PATH = '/home/xilinx/AI/old_null_5f_2.bit'
CSV_FILENAME = '/home/xilinx/AI/post_processed_features_old_null_5f_2.csv'

def run_setup_script():
    # Execute setup script directly
    subprocess.run(['/bin/bash', '-c', 'source /etc/profile.d/xrt_setup.sh'])

def test_model():
    overlay = Overlay(BITSTREAM_PATH)
    dma_send = overlay.axi_dma_0.sendchannel
    dma_recv = overlay.axi_dma_0.recvchannel
    mlp_ip = overlay.mlp_0

    # Allocate buffers
    buffer_size = FEATURES * SENSOR_NUM
    in_buffer = allocate(shape=(buffer_size,), dtype=np.int32)
    out_buffer = allocate(shape=(1,), dtype=np.int32)

    counter = 0
    with open(CSV_FILENAME, 'r') as csv_file:
        csv_reader = csv.reader(csv_file)

        start_time = time.time()
        for row in csv_reader:
            features = [float(value) for value in row]
            in_buffer[:] = features

            dma_send.transfer(in_buffer)
            dma_recv.transfer(out_buffer)
            dma_send.wait()
            dma_recv.wait()

            received_data = int(out_buffer[0])
            # Compare received_data with expected_preds[index] if available
            # Increment counter accordingly

        time_taken = time.time() - start_time
        accuracy = (counter * 100) / DATASET_SIZE
        print("Final accuracy:", accuracy)
        print("Time taken:", time_taken, "seconds")

    # Deallocate buffers
    # in_buffer.freebuffer()
    # out_buffer.freebuffer()

if __name__ == "__main__":
    run_setup_script()
    test_model()

